import React, { useState } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:8000';

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() && !selectedFile) return;

    setLoading(true);

    try {
      let response;
      let newMessage;

      if (selectedFile) {
        // Handle image upload with better error handling
        const formData = new FormData();
        formData.append('file', selectedFile);

        // Add message as form data if provided
        if (input.trim()) {
          formData.append('message', input);
        }

        console.log('Sending image:', {
          name: selectedFile.name,
          size: selectedFile.size,
          type: selectedFile.type
        });

        response = await fetch(`${BACKEND_URL}/chat/image`, {
          method: 'POST',
          body: formData,
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(`HTTP ${response.status}: ${errorData.detail || 'Unknown error'}`);
        }

        const data = await response.json();
        newMessage = {
          type: 'user',
          content: input || 'Uploaded an image',
          image: URL.createObjectURL(selectedFile),
          timestamp: new Date().toLocaleTimeString()
        };

        setMessages(prev => [...prev, newMessage, {
          type: 'bot',
          content: data.response,
          info: data.image_info,
          timestamp: new Date().toLocaleTimeString()
        }]);

        setSelectedFile(null);

      } else {
        // Handle text message
        console.log('Sending text message:', input);

        response = await fetch(`${BACKEND_URL}/chat`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ message: input }),
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(`HTTP ${response.status}: ${errorData.detail || 'Unknown error'}`);
        }

        const data = await response.json();
        newMessage = {
          type: 'user',
          content: input,
          timestamp: new Date().toLocaleTimeString()
        };

        setMessages(prev => [...prev, newMessage, {
          type: 'bot',
          content: data.response,
          timestamp: new Date().toLocaleTimeString()
        }]);
      }

      setInput('');
    } catch (error) {
      console.error('Error:', error);
      let errorMessage = error.message;

      // Provide specific help for common errors
      if (errorMessage.includes('cannot identify image')) {
        errorMessage = 'Image format not supported. Please try uploading a JPEG, PNG, or GIF image.';
      } else if (errorMessage.includes('Network')) {
        errorMessage = `Cannot connect to backend at ${BACKEND_URL}. Make sure the backend server is running.`;
      }

      setMessages(prev => [...prev, {
        type: 'bot',
        content: `❌ Error: ${errorMessage}`,
        timestamp: new Date().toLocaleTimeString()
      }]);
    }

    setLoading(false);
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file type
      if (!file.type.startsWith('image/')) {
        alert('Please select an image file (JPEG, PNG, GIF, etc.)');
        return;
      }

      // Check file size (max 10MB)
      if (file.size > 10 * 1024 * 1024) {
        alert('File size too large. Please select an image smaller than 10MB.');
        return;
      }

      console.log('Selected file:', {
        name: file.name,
        size: file.size,
        type: file.type
      });

      setSelectedFile(file);
    }
  };

  const removeSelectedFile = () => {
    setSelectedFile(null);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>🤖 Gemini AI Chatbot</h1>
        <p>Chat with AI and upload images for analysis (Fixed Version)</p>
      </header>

      <div className="chat-container">
        <div className="messages">
          {messages.length === 0 && (
            <div className="welcome-message">
              <h3>Welcome to Gemini AI Chatbot!</h3>
              <p>✨ Send a message or upload an image to get started</p>
              <p>🖼️ Supported: JPEG, PNG, GIF (max 10MB)</p>
              <p>🔗 Backend: {BACKEND_URL}</p>
            </div>
          )}
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.type}`}>
              <div className="message-content">
                {message.image && (
                  <img 
                    src={message.image} 
                    alt="Uploaded" 
                    className="uploaded-image"
                  />
                )}
                <p>{message.content}</p>
                {message.info && (
                  <small className="image-info">{message.info}</small>
                )}
                <span className="timestamp">{message.timestamp}</span>
              </div>
            </div>
          ))}
          {loading && (
            <div className="message bot">
              <div className="message-content">
                <p>🤔 {selectedFile ? 'Analyzing image...' : 'Thinking...'}</p>
              </div>
            </div>
          )}
        </div>

        <form onSubmit={sendMessage} className="input-form">
          {selectedFile && (
            <div className="selected-file">
              <img 
                src={URL.createObjectURL(selectedFile)} 
                alt="Selected" 
                className="preview-image"
              />
              <div className="file-info">
                {selectedFile.name} ({(selectedFile.size / 1024).toFixed(1)}KB)
              </div>
              <button 
                type="button" 
                onClick={removeSelectedFile}
                className="remove-file"
              >
                ❌
              </button>
            </div>
          )}

          <div className="input-row">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder={selectedFile ? "Ask about the image..." : "Type your message..."}
              disabled={loading}
            />

            <label htmlFor="file-input" className="file-input-label" title="Upload image">
              📎
            </label>
            <input
              id="file-input"
              type="file"
              accept="image/*"
              onChange={handleFileSelect}
              className="file-input"
            />

            <button type="submit" disabled={loading || (!input.trim() && !selectedFile)}>
              {loading ? '...' : '📤'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
